tinymce.PluginManager.requireLangPack("mc_pages");tinymce.PluginManager.add("mc_pages",(function(editor,url){const _=text=>editor.translate(text);const iconPath=typeof baseadmin!=="undefined"?"/"+baseadmin+"/template/img/tinymce/mc_icons.svg#page":url+"/img/mc_icons.svg#page";editor.ui.registry.addIcon("page",`<svg width="24" height="24"><use xlink:href="${iconPath}"></use></svg>`);const showDialog=()=>{editor.windowManager.openUrl({title:_("mc_pages Title"),url:tinymce.baseURL+"/plugins/mc_pages/pages.php",width:800,height:550})};editor.ui.registry.addButton("mc_pages",{icon:"page",tooltip:_("mc_pages Tooltip"),onAction:showDialog,onSetup:buttonApi=>{const nodeChangeHandler=eventApi=>{buttonApi.setEnabled(eventApi.element.nodeName!=="IMG")};editor.on("NodeChange",nodeChangeHandler);return()=>editor.off("NodeChange",nodeChangeHandler)}});editor.ui.registry.addMenuItem("mc_pages",{icon:"page",text:_("mc_pages Title"),onAction:showDialog});return{getMetadata:()=>({name:"Magix CMS Pages",author:"Gerits Aurelien",version:"2.0.0"})}}));